package loopCodition;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="Raja";
		for (int i = 0; i < 5; i++) {
			System.out.println(name);
		}
		
		
		int i =0;
		
		while (i<5) {
			System.out.println("vicky");
			i++; 
		}
	
		int j=0;
		do {
			System.out.println("Bala");
			j++; 
		} while (j<5);

	}

}
