package switchCondition;

public class MyClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String Hero ="SuperMan";
		
		switch (Hero) {
		case "BatMan":
			System.out.println("BatMan is hero");

		case "SuperMan":
			System.out.println("SuperMan is hero");
			
		default:
			System.out.println("BatMan Not a hero");
		
		}

	}

}
