package switchCondition;

public class UsingBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String Hero = "Iron Man";
		
		switch (Hero) {
		case "Vision":
			System.out.println("Vision is a Hero");
			break;
			
		case"Black Panther":
			System.out.println("Black Panther is a Hero");
			break;
			
		case"Loki":
			System.out.println("Loki is a Hero");
			break;
			
		case"Captain America":
			System.out.println("Captain America is a Hero");
			break;
		case"Iron Man":
			System.out.println("Iron Man is a Hero");
			break;

		default:
			System.out.println("Above All Are not A hero");
			break;
		}

	}

}
