package conditionStatement;

public class IfConditionUsingString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String Name ="Raja";
		
		if (Name.equalsIgnoreCase("raja")) {
			System.out.println("This name is raja");
		}else {
			System.out.println("This is not a raja");
		}

	}

}
